    public static int majorityElement(int[] nums) {
        if (nums.length == 1) {
            return nums[0];
        }

        List<Integer> list = new ArrayList<>();

        for (int num : nums) {
            list.add(num);
        }

        Collections.sort(list);

        int size1 = 0;
        int sizeMax = 0;
        int result;
        int resultMax = 0;

        for (int i = 1; i < list.size(); i++) {
            if (list.get(i).equals(list.get(i - 1))) {

                size1++;
                result = list.get(i);

            } else {
                result = 0;
                size1 = 0;
            }
            if (sizeMax < size1) {
                resultMax = result;
                sizeMax = size1;
            }
        }
        return resultMax;
    }
